mapper.select:
select c.*, u.*, (select count(*) from watching where watching_movie_id=c.comment_movie_id and watching_user_id=c.comment_user_id) as is_watching, (select count(*) from wish where wish_movie_id=c.comment_movie_id and wish_user_id=c.comment_user_id) as is_wish, ifnull((select rate_star from rate where rate_user_id=c.comment_user_id and rate_movie_id=c.comment_movie_id), 0) as comment_rate from comment c join user u on comment_user_id = user_id where
comment_id=#{id}
comment_user_id=#{userId} and comment_movie_id=#{movieId}

select c.*, u.*, (select count(*) from pandora3_ojt.watching where watching_movie_id=c.comment_movie_id and watching_user_id=c.comment_user_id) as is_watching, (select count(*) from pandora3_ojt.wish where wish_movie_id=c.comment_movie_id and wish_user_id=c.comment_user_id) as is_wish, ifnull((select rate_star from rate where rate_user_id=c.comment_user_id and rate_movie_id=c.comment_movie_id), 0) as comment_rate from comment c join user u on comment_user_id = user_id where comment_movie_id=#{movieId}
order by comment_id desc
order by comment_like_count desc
order by comment_rate desc
limit #{limit} offset #{offset}
